function jsonform(b){var a={};b.each(function(){if($(this).attr("type")=="checkbox"){if($(this).attr("checked")){if(a[$(this).attr("name")]){a[$(this).attr("name")].push($(this).val())}else{a[$(this).attr("name")]=[$(this).val()]}}}else{if($(this).attr("type")=="radio"){if($(this).attr("checked")){a[$(this).attr("name")]=$(this).val()}}else{if($.inArray($(this).attr("type"),["button","submit","reset"])===-1){a[$(this).attr("name")]=$(this).val()}}}});return JSON.stringify(a)};